import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';
import 'package:url_launcher/url_launcher.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      home: MyGridBody(),
    );
  }
}

class MyGridBody extends StatefulWidget {
  const MyGridBody({Key? key}) : super(key: key);

  @override
  _MyGridBodyState createState() => _MyGridBodyState();
}

class _MyGridBodyState extends State<MyGridBody> {
  List gridItem = [
    'Anamul',
    'Ratan',
    'Nayeem',
    'Shuvo',
    'Ashik',
    'Lokman',
    'Minhaz',
  ];
  List img = [
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRkTFzOBb9ogv-TdtjKS8g0cXfuxcB6uSBjKA&usqp=CAU',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRkTFzOBb9ogv-TdtjKS8g0cXfuxcB6uSBjKA&usqp=CAU',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSwhVU9fj8qyInZ3hf6e4upw2XJCl3-cPT3Kg&usqp=CAU',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSRR3rRiMu6znXqGwJi9AEo_ZAIPE32FhG-Jg&usqp=CAU',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTE9A9P4MN8paTCoE9vV15AdKAremX6NiQujg&usqp=CAU',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,'
  ];
  final link = [
    'https://facebook.com',
    'https://instagram.com',
    'https://google.com',
    'https://pub.dev',
    'https://flutter.dev',
    'https://facebook.com',
    'https://facebook.com',
  ];

  _launchURL(index) async => await canLaunch(link[index])
      ? await launch(link[index])
      : throw 'Could not launch $link[index]';

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: GridView.builder(
          itemCount: gridItem.length,
          gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
            crossAxisCount: 2,
            mainAxisSpacing: 5,
            crossAxisSpacing: 5,
          ),
          itemBuilder: (context, int index) {
            return GestureDetector(
              child: Card(
                color: Colors.teal[100],
                elevation: 10,
                child: Column(
                  children: [
                    Image.network(img[index]),
                    Text(
                      gridItem[index],
                      style: const TextStyle(fontWeight: FontWeight.bold),
                    ),
                    const Text(
                      'In publishing and graphic design, Lorem..',
                      textAlign: TextAlign.justify,
                      style: TextStyle(color: Colors.blueGrey),
                    ),
                  ],
                ),
              ),
              onDoubleTap: () {
                _launchURL(index);
              },
              onTap: () {
                showDialog(
                    context: context,
                    builder: (context) {
                      return AlertDialog(
                        title: Column(
                          children: const [
                            Icon(
                              Icons.favorite,
                              color: Colors.red,
                              size: 40,
                            ),
                            Text(
                              'In publishing and graphic design, Lorem ipsum is a placeholder text commonly used to demonstrate the visual form of a document or a typeface without',
                              textAlign: TextAlign.justify,
                              style: TextStyle(color: Colors.blueGrey),
                            ),
                          ],
                        ),
                        content: Text(
                            'Double tap to visit link' ' ' + gridItem[index]),
                      );
                    });
              },
            );
          }),
    );
  }
}
